SELECT 'cargos',
	(SELECT CD_EMPRESA FROM CAD_EMPRESACONFIG WHERE CAD_EMPRESACONFIG.ID_EMPRESA = CAD_ALTERACAOCARGOSERVICO.ID_EMPRESA ) AS CODI_EMP,
	CAD_ALTERACAOCARGOSERVICO.ID_EMPRESA,
	CAD_ALTERACAOCARGOSERVICO.CD_AUTONOMO,
	CAD_ALTERACAOCARGOSERVICO.DT_VIGENCIAINICIO,
	CAD_ALTERACAOCARGOSERVICO.DS_PROFISSAO
FROM CAD_ALTERACAOCARGOSERVICO
WHERE FG_TIPOCADASTRO = 'ALTERACAO SERVICO'
ORDER BY 1,2,3,4