SELECT 'salarios' ,
	(SELECT CD_EMPRESA FROM CAD_EMPRESACONFIG WHERE CAD_EMPRESACONFIG.ID_EMPRESA = CAD_ALTERACAOCARGOSALARIO.ID_EMPRESA ) AS CODI_EMP,
	CAD_ALTERACAOCARGOSALARIO.ID_EMPRESA,
	CAD_ALTERACAOCARGOSALARIO.CD_FUNCIONARIO,
	DT_VIGENCIAINICIO AS DATA_TROCA,
	CAD_ALTERACAOCARGOSALARIO.VR_SALARIO,
	CAD_ALTERACAOCARGOSALARIO.DS_MOTIVOALTERACAO
FROM CAD_ALTERACAOCARGOSALARIO
WHERE FG_TIPOCADASTRO = 'ALTERACAO SALARIAL'
ORDER BY 2,3,4,5