SELECT 'salarios' ,
	(SELECT CD_EMPRESA FROM CAD_EMPRESACONFIG WHERE CAD_EMPRESACONFIG.ID_EMPRESA = CAD_ALTERACAOCARGOREMUNERACAO.ID_EMPRESA ) AS CODI_EMP,
	CAD_ALTERACAOCARGOREMUNERACAO.ID_EMPRESA,
	CAD_ALTERACAOCARGOREMUNERACAO.CD_SOCIO,
	DT_VIGENCIAINICIO AS DATA_TROCA,
	CAD_ALTERACAOCARGOREMUNERACAO.VR_SALARIO,
	NULL
FROM CAD_ALTERACAOCARGOREMUNERACAO
WHERE FG_TIPOCADASTRO = 'ALTERACAO RETIRADA'
ORDER BY 2,3,4,5