SELECT DISTINCT
       TB_CADHISTORICOSALARIAL_HAS.COD_EMP,
       TB_CADCARGO_CCG.COD_CCG,
       TB_CADCARGO_CCG.DESCRICAO_CCG,
       TB_CADCARGO_CCG.CBO_CCG
 FROM TB_CADCARGO_CCG
 LEFT JOIN TB_CADHISTORICOSALARIAL_HAS
   ON TB_CADHISTORICOSALARIAL_HAS.COD_CCG = TB_CADCARGO_CCG.COD_CCG
 LEFT JOIN TB_CADFUNCIONARIO_FUN
   ON TB_CADFUNCIONARIO_FUN.COD_CCG = TB_CADCARGO_CCG.COD_CCG
WHERE TB_CADHISTORICOSALARIAL_HAS.COD_CCG IS NOT NULL
UNION
SELECT DISTINCT
       TB_CADFUNCIONARIO_FUN.COD_EMP,
       TB_CADCARGO_CCG.COD_CCG,
       TB_CADCARGO_CCG.DESCRICAO_CCG,
       TB_CADCARGO_CCG.CBO_CCG
 FROM TB_CADCARGO_CCG
 LEFT JOIN TB_CADFUNCIONARIO_FUN
   ON TB_CADFUNCIONARIO_FUN.COD_CCG = TB_CADCARGO_CCG.COD_CCG
WHERE TB_CADFUNCIONARIO_FUN.COD_CCG IS NOT NULL
ORDER BY 1, 2;