SELECT COD_EMP,
       EVENTO_TET,
       COD_CCG AS CODIGO,
       NRRECIBO_TET,
       NULL AS DATA_ALT
  FROM TB_EVT_TABELAS_TET
 WHERE NRRECIBO_TET <> ''
   AND EVENTO_TET IN ('S-1000', 'S-1030')
UNION
SELECT COD_EMP,
       EVENTO_TENP,
       COD_FUN AS CODIGO,
       NRRECIBO_TENP,
       CAST(DATA_ALTERACAO_TENP AS DATE) AS DATA_ALT
  FROM TB_EVT_NAOPERIODICOS_TENP
 WHERE NRRECIBO_TENP != ''
   AND COD_FUN IS NOT NULL
   AND EVENTO_TENP IN ('S-2200', 'S-2205', 'S-2206', 'S-2300', 'S-2305', 'S-2306')
ORDER BY 1, 2, 3, 4, 5;