SELECT TB_CADFUNCIONARIO_FUN.*,
       (SELECT MAX(DT_AFASTAMENTO_RCT)
          FROM TB_CADRESCISAO_RCT 
         WHERE TB_CADRESCISAO_RCT.COD_EMP = TB_CADFUNCIONARIO_FUN.COD_EMP
           AND TB_CADRESCISAO_RCT.COD_FUN = TB_CADFUNCIONARIO_FUN.COD_FUN) AS DATA_DEMISSAO
  FROM TB_CADFUNCIONARIO_FUN
 ORDER BY 1, 4;