SELECT DISTINCT
       TB_CADFUNCIONARIO_FUN.COD_EMP,
       TB_CADHORARIO_CHR.TURNO1_ENTRADA_CHR,
       TB_CADHORARIO_CHR.TURNO1_SAIDA_CHR,
       TB_CADHORARIO_CHR.TURNO2_ENTRADA_CHR,
       TB_CADHORARIO_CHR.TURNO2_SAIDA_CHR
  FROM TB_CADFUNCIONARIO_FUN
  LEFT JOIN TB_CADHORARIO_CHR
    ON TB_CADHORARIO_CHR.COD_CHR = TB_CADFUNCIONARIO_FUN.COD_CHR
    OR TB_CADHORARIO_CHR.COD_CHR = TB_CADFUNCIONARIO_FUN.COD_CHR1
    OR TB_CADHORARIO_CHR.COD_CHR = TB_CADFUNCIONARIO_FUN.COD_CHR2
    OR TB_CADHORARIO_CHR.COD_CHR = TB_CADFUNCIONARIO_FUN.COD_CHR3
    OR TB_CADHORARIO_CHR.COD_CHR = TB_CADFUNCIONARIO_FUN.COD_CHR4
    OR TB_CADHORARIO_CHR.COD_CHR = TB_CADFUNCIONARIO_FUN.COD_CHR5
 WHERE TB_CADHORARIO_CHR.COD_CHR IS NOT NULL;