SELECT ABREV,
	   CODFUNC AS CODIGO,
	   SITUACAO,
	   DTAFASTEFET AS DATA_INICIO,
	   DTRETORNO,
	   'empregado' AS TIPO_FUNC
FROM AFAST

UNION

SELECT ABREV,
	   CODSOC AS CODIGO,
	   SITUACAO,
	   DTAFAST AS DATA_INICIO,
	   DTRETORNO,
	   'contribuinte' AS TIPO_FUNC
FROM AFASTSOC
ORDER BY 1, 2, 4