SELECT EMPRESAS.*,
       PARAMFP.NATJURIDICA,
       PARAMFP.DTINICIO,
       PARAMFP.CNAEPREPONDERANTE,
       PARAMFP.CNAECAGED,
       PARAMFP.NOMERESPONSAVEL,
       PARAMFP.CPFRESPONSAVEL,
       PARAMFP.RGRESPONSAVEL,
       PARAMFP.CODFPAS,
       PARAMFP.CODTERC,
       PARAMFP.CODGPS,
       (SELECT MIN(f.DTADMIT) FROM FUNCIO f WHERE f.ABREV = EMPRESAS.ABREV) AS ADMISSAO_EMPREGADO,
       (SELECT MIN(s.DTADMISSAO) FROM SOCIOS s WHERE s.ABREV = EMPRESAS.ABREV) AS ADMISSAO_SOCIO,
       PARAMFP.EMAIL AS EMAILEMPRESA,
       PARAMFP.FONEFIXO AS FONEFIXO,
       PARAMFP.FONECELULAR AS FONECELULAR,
       PARAMFP.CODMUNICIPIO,
       PARAMFP.CAPITALEMPR
FROM EMPRESAS
INNER JOIN PARAMFP
ON EMPRESAS.ABREV = PARAMFP.ABREV
ORDER BY 1