SELECT ABREV,
      CHAVEEVENTO,
      NUMERORECIBO,
      CHAVEEVENTO AS MATRICULA,
	  EVENTO
FROM GERENCIAESOCIAL
WHERE EVENTO in ('S1000', 'S1030', 'S2300')
AND LEFT(NUMERORECIBO,3) = '1.1'

UNION

SELECT g.ABREV,
      f.CODFUNC AS CHAVEEVENTO,
      g.NUMERORECIBO,
      f.NUMREGISTRO AS MATRICULA,
	  g.EVENTO
FROM GERENCIAESOCIAL AS g
INNER JOIN FUNCIO AS f
ON f.ABREV = g.ABREV
AND f.CODFUNC = g.CAMPOPESQUISA
WHERE g.EVENTO in ('S2200')
AND LEFT(g.NUMERORECIBO,3) = '1.1'
ORDER BY 1, 5

