SELECT ABREV,
       CODFUNC AS CODIGO,
       NUMPLSAUDE,
       ANO,
       MES,
       ANOVALIDADE,
       MESVALIDADE,
       'empregado' AS TIPO_FUNC,
		CASE 
			WHEN EXISTS (SELECT 1 
						  FROM DEPLSAUD sub 
						 WHERE sub.ABREV = t.ABREV 
						   AND sub.CODFUNC = t.CODFUNC)
			THEN 1 
			ELSE 0 
		END AS DEPENDENTE
		FROM TIPLSAUD t

UNION

SELECT ABREV,
       CODSOC AS CODIGO,
       NUMPLSAUDE,
       ANO,
       MES,
       ANOVALIDADE,
       MESVALIDADE,
       'contribuinte' AS TIPO_FUNC,
		CASE 
			WHEN EXISTS (SELECT 1 
						  FROM DEPLSAUDSOC sub 
						 WHERE sub.ABREV = t.ABREV 
						   AND sub.CODSOC = t.CODSOC)
			THEN 1 
			ELSE 0 
		END AS DEPENDENTE
		FROM TIPLSAUDSOC t
ORDER BY 1, 2, 4