SELECT trocas.MVHOR_CODEMP, 
	   trocas.MVHOR_CDFUNC, 
	   GER_HORARIO_SEMANAL.HOR_DESC,
	   trocas.MVHOR_HORAR_TRABALHO AS I_JORNADAS,
	   trocas.MVHOR_DATA, 
	   trocas.MVHOR_TP_HORAR_TRAB
FROM RH_HORFUNC trocas 
LEFT JOIN GER_HORARIO_SEMANAL 
ON trocas.MVHOR_HORAR_TRABALHO = GER_HORARIO_SEMANAL.HOR_COD
WHERE trocas.MVHOR_HORAR_TRABALHO IS NOT null 					    
ORDER BY 1,2,5,4