    SELECT GER_MOVFUNC.*,SAU_DADOSFNC.DDFNC_CODFNC AS codigo_cargo, FNC_DESC, FNC_CODCBO
      FROM GER_MOVFUNC
LEFT JOIN SAU_DADOSFNC 
        ON GER_MOVFUNC.MVFU_CODEMP = SAU_DADOSFNC.DDFNC_CODEMP
       AND GER_MOVFUNC.MVFU_DDFNCSEQ = SAU_DADOSFNC.DDFNC_SEQ
LEFT JOIN GER_FUNCOES ON GER_FUNCOES.FNC_COD = SAU_DADOSFNC.DDFNC_CODFNC
  ORDER BY 1,4,2,3