SELECT TBLASO.CODEMPFOL,
TBLASO.MATEPGCTT,
TBLASO.CODASO,
TBLASO.CODTIPASO,
TBLASO.CODRESASO,
TBLASO.DTAASO,
NULL AS DATA_VENCIMENTO,
TBLPES.NOMFUN,
TBLMEDRSP.CRMMEDRSP,
TBLMEDRSP.CODPESMEDRSP
FROM TBLASO
LEFT JOIN tblpes
ON tblpes.CODPES = TBLASO.CODPES
AND TBLPES.TIPPES = TBLASO.TIPPES
LEFT JOIN TBLMEDRSP
    ON TBLMEDRSP.CODPESMEDRSP = tblpes.CODPES
   AND TBLMEDRSP.TIPPESMEDRSP = tblpes.TIPPES
   
UNION

 SELECT TBLEXAMEDCLI.CODEMPFOL,
TBLEXAMEDCLI.MATEPGCTT,
TBLEXAMEDCLI.CODASO,
TBLEXAMEDCLI.TIPEXAMED,
TBLEXAMEDCLI.RESEXACLIPER,
TBLEXAMEDCLI.DTAEXACLIPER,       
TBLEXAMEDCLI.DTAVENEXACLIPER,       
TBLPES.NOMFUN,
TBLMEDRSP.CRMMEDRSP,
TBLMEDRSP.CODPESMEDRSP
FROM TBLEXAMEDCLI
LEFT JOIN tblpes
ON tblpes.CODPES = TBLEXAMEDCLI.CODPES
AND TBLPES.TIPPES = TBLEXAMEDCLI.TIPPES
LEFT JOIN TBLMEDRSP
    ON TBLMEDRSP.CODPESMEDRSP = tblpes.CODPES
   AND TBLMEDRSP.TIPPESMEDRSP = tblpes.TIPPES
ORDER BY 1, 2, 6