SELECT TBLPER.CODEMP,
	   TBLPLACNT.CODREDCNT,
	   TBLPLACNT.CODNORCNT,
	   TBLPLACNT.DSCCNT,
	   TBLPLACNT.NATCNT,
	   TBLPLACNT.ANASINCNT 
FROM TBLPER INNER JOIN TBLPLACNT
					ON TBLPER.CODCFGCTB = TBLPLACNT.CODCFGCTB
			
WHERE TBLPER.DTAINIPER = (SELECT FIRST(1) SUB.DTAINIPER
							FROM TBLPER AS SUB
						   WHERE SUB.CODEMP = TBLPER.CODEMP
						  ORDER BY SUB.DTAINIPER DESC)
ORDER BY 1,2