SELECT (SELECT MAX(CODEMPFOL) FROM TBLCTT WHERE TBLCTT.CODPESEPG = TBLDPDEPG.CODPESEPG AND TBLCTT.TIPPESEPG = TBLDPDEPG.TIPPESEPG) AS CODEMPFOL,
	   (SELECT MAX(MATEPGCTT) FROM TBLCTT WHERE TBLCTT.CODPESEPG = TBLDPDEPG.CODPESEPG AND TBLCTT.TIPPESEPG = TBLDPDEPG.TIPPESEPG) AS MATEPGCTT,
	    CODDPDEPG,
		NOMDEPEPG,
		DTANSCDPDEPG,
		GRAPARDPDEPG,
		REGDPDEPG,
		LIVFOLDPDEPG,
		DEPSALFAMDPDEPG,
		DEPIRRDPDEPG,
		NATDPDEPG,
		INSDPD,
		CARDPDEPG
FROM TBLDPDEPG
ORDER BY 1, 2, 3