SELECT TBLITEMOVMEN.CODEMPFOL,
	   TBLMOVMEN.MATEPGCTT,
	   TBLITEMOVMEN.VLRTOTEVEITEMOVMEN,
	   TBLMOVMEN.MESANOINCMOVMEN,
	   TBLITEMOVMEN.CODEVE
FROM TBLITEMOVMEN

INNER JOIN TBLEVE
ON TBLEVE.CODEVE = TBLITEMOVMEN.CODEVE, TBLMOVMEN

WHERE TBLEVE.DSCEVE LIKE '%CONTRIBUICAO SIND%'
	AND TBLITEMOVMEN.CODEMPFOL = TBLMOVMEN.CODEMPFOL
	AND TBLITEMOVMEN.CODSITEPG = TBLMOVMEN.CODSITEPG
	AND TBLITEMOVMEN.SEQMOVMEN = TBLMOVMEN.SEQMOVMEN
	AND TBLITEMOVMEN.CODTIPFOL = TBLMOVMEN.CODTIPFOL
	AND TBLITEMOVMEN.MESANOFATGERMOVMEN = TBLMOVMEN.MESANOFATGERMOVMEN
ORDER BY TBLITEMOVMEN.CODEMPFOL, TBLMOVMEN.MATEPGCTT, TBLMOVMEN.MESANOINCMOVMEN
