SELECT CODEMPFOL,
	   MATEPGCTT,
	   DTAINIGOZPRGFRS,
	   DTAFINGOZPRGFRS,
	   DTAINIABOPRGFRS,
	   DTAFINABOPRGFRS,
	   (DTAINIGOZPRGFRS - 2) AS DATA_PAGTO,
	   DTAINIAQUPRGFRS,
	   DTAFINAQUITEPRGFRS
FROM TBLITEPRGFRS
ORDER BY TBLITEPRGFRS.CODEMPFOL, TBLITEPRGFRS.MATEPGCTT, TBLITEPRGFRS.DTAINIAQUPRGFRS