SELECT TBLMEDRSP.CODPESMEDRSP,
       TBLMEDRSP.CRMMEDRSP,
       TBLMEDRSP.CODUNDFEDEXPCRM,
       tblpes.NOMFUN,
       tblpes.INSPES
  FROM TBLMEDRSP LEFT OUTER JOIN tblpes
    ON TBLMEDRSP.CODPESMEDRSP = tblpes.CODPES
   AND TBLMEDRSP.TIPPESMEDRSP = tblpes.TIPPES
 WHERE TBLMEDRSP.TIPPESMEDRSP = 'M' /* MÃ©dico */
ORDER BY 1