SELECT TBLPER.CODEMP,
	   TBLPER.DTAINIPER,
	   TBLPER.CODCFGCTB,
	   TBLMSCCNT.MSCEDIMSCCNT 
FROM TBLPER INNER JOIN TBLCFGCTB
					ON TBLPER.CODCFGCTB = TBLCFGCTB.CODCFGCTB
			INNER JOIN TBLMSCCNT
					ON TBLMSCCNT.CODMSCCNT = TBLCFGCTB.CODMSCCNT
WHERE TBLPER.DTAINIPER = (SELECT FIRST(1) SUB.DTAINIPER
							FROM TBLPER AS SUB
						   WHERE SUB.CODEMP = TBLPER.CODEMP
						  ORDER BY SUB.DTAINIPER DESC)
ORDER BY 1,2