SELECT DISTINCT
       TBLMOVMEN.CODEMPFOL,
       TBLEVE.CODEVE,
       TBLEVE.DSCEVE,
       TBLEVE.ACEREF1EVE,
       TBLEVE.ACEREF2EVE,
       TBLSINEVEMED.TIPSINEVEMED
  FROM TBLITEMOVMEN
 INNER JOIN TBLMOVMEN
    ON TBLITEMOVMEN.CODSITEPG = TBLMOVMEN.CODSITEPG
   AND TBLITEMOVMEN.SEQMOVMEN = TBLMOVMEN.SEQMOVMEN
   AND TBLITEMOVMEN.CODEMPFOL = TBLMOVMEN.CODEMPFOL
   AND TBLITEMOVMEN.CODTIPFOL = TBLMOVMEN.CODTIPFOL
   AND TBLITEMOVMEN.MESANOFATGERMOVMEN = TBLMOVMEN.MESANOFATGERMOVMEN
 INNER JOIN TBLCTT
    ON TBLCTT.CODEMPFOL = TBLMOVMEN.CODEMPFOL
   AND TBLCTT.MATEPGCTT = TBLMOVMEN.MATEPGCTT
  LEFT JOIN TBLSINEVEMED
    ON TBLSINEVEMED.CODEVE = TBLITEMOVMEN.CODEVE
   AND TBLSINEVEMED.CODSIN = TBLCTT.CODSIN
 INNER JOIN TBLEVE
    ON TBLEVE.CODEVE = TBLITEMOVMEN.CODEVE
 WHERE TBLEVE.TIPEVE = 'P'
 ORDER BY 1, 2;