SELECT TBLSCORSP.*,
       (SELECT TBLCRG.DSCCRG
          FROM TBLCRG
         WHERE TBLSCORSP.CODCRG = TBLCRG.CODCRG) AS DESCCAR,
	   (SELECT MAX(cid.NOMCID)
		   FROM TBLCID as cid
		  WHERE cid.codcid = TBLSCORSP.codcid ) as CIDADE,
       (SELECT MAX(cid.CODUNDFED)
		  FROM TBLCID as cid
		 WHERE cid.codcid = TBLSCORSP.codcid ) as ESTADO	 
  FROM TBLSCORSP 
ORDER BY 1