SELECT TBLCTTOCUPRO.CODEMPFOL,
       TBLCTTOCUPRO.MATEPGCTT,
       TBLCTTOCUPRO.DTACTTOCUPRO,
       TBLCTTOCUPRO.CODOCUPRO
  FROM TBLCTTOCUPRO
  LEFT JOIN TBLCTT
    ON TBLCTT.CODEMPFOL = TBLCTTOCUPRO.CODEMPFOL
   AND TBLCTT.MATEPGCTT = TBLCTTOCUPRO.MATEPGCTT
 WHERE TBLCTT.DTAADMTRACTT != TBLCTTOCUPRO.DTACTTOCUPRO
   AND TBLCTTOCUPRO.DTACTTOCUPRO > '1900-01-01'
 ORDER BY 1, 2, 3;
