SELECT TBLCTTSEC.CODEMPFOL,
       TBLCTTSEC.MATEPGCTT,
       TBLCTTSEC.DTACTTSEC,
       TBLCTTSEC.CODSEC
  FROM TBLCTTSEC
  LEFT JOIN TBLCTT
    ON TBLCTT.CODEMPFOL = TBLCTTSEC.CODEMPFOL
   AND TBLCTT.MATEPGCTT = TBLCTTSEC.MATEPGCTT
 WHERE TBLCTT.DTAADMTRACTT != TBLCTTSEC.DTACTTSEC
 ORDER BY 1, 2, 3;