SELECT TBLCTTDEP.CODEMPFOL,
       TBLCTTDEP.MATEPGCTT,
       TBLCTTDEP.DTACTTDEP,
       TBLCTTDEP.CODDEP
  FROM TBLCTTDEP
  LEFT JOIN TBLCTT
    ON TBLCTT.CODEMPFOL = TBLCTTDEP.CODEMPFOL
   AND TBLCTT.MATEPGCTT = TBLCTTDEP.MATEPGCTT
 WHERE TBLCTT.DTAADMTRACTT != TBLCTTDEP.DTACTTDEP
 ORDER BY 1, 2, 3;