SELECT TBLHORTRBCTT.CODEMPFOL,
       TBLHORTRBCTT.MATEPGCTT,
       TBLHORTRBCTT.DTAHORTRBCTT,
       TBLHORTRBCTT.CODHORTRB
  FROM TBLHORTRBCTT
  LEFT JOIN TBLCTT
    ON TBLCTT.CODEMPFOL = TBLHORTRBCTT.CODEMPFOL
   AND TBLCTT.MATEPGCTT = TBLHORTRBCTT.MATEPGCTT
 WHERE TBLCTT.DTAADMTRACTT != TBLHORTRBCTT.DTAHORTRBCTT
ORDER BY 1, 2, 3;