SELECT AFAST.FK_PESSOA,
       AFAST.FK_FUNCIONARIO,
       AFAST.DATA,
       AFAST.DATAFIN,
       PFUNCIONARIO_SITUACAO_COD.CODIGO,
       PFUNCIONARIO_SITUACAO_COD.CODIGO_MOVIMENTACAO_FGTS
FROM PFUNCIONARIO_SITUACAO AS AFAST
INNER JOIN PFUNCIONARIO_SITUACAO_COD
	ON PFUNCIONARIO_SITUACAO_COD.ID_PFUNCIONARIO_SITUACAO_COD = AFAST.FK_CODIGO_SITUACAO
ORDER BY 1, 2, 3