SELECT AFAST.FK_PESSOA,
	   AFAST.FK_FUNCIONARIO,
       MAX(FER.DATA_FIN_AQUISITIVO) AS ULTIMO_AQUISITIVO
FROM PFUNCIONARIO_SITUACAO_FE AS FER
LEFT JOIN PFUNCIONARIO_SITUACAO AS AFAST
	ON AFAST.FK_PESSOA = FER.FK_PESSOA
	AND AFAST.ID_PFUNCIONARIO_SITUACAO = FER.FK_FUNCIONARIO_SITUACAO
GROUP BY 1, 2