SELECT PAUTONOMOS.FK_PESSOA,
       PAUTONOMOS.ID_PAUTONOMOS,
       PAUTONOMOS.NOME,
       PAUTONOMOS.DATA_NASCIMENTO,
       PAUTONOMOS.INSCMF,
       PAUTONOMOS.INSC_INSS,
       PCBO.DESCRICAO AS CARGO,
       PCBO.CODIGO AS CARGO_CBO
FROM PAUTONOMOS
LEFT JOIN PCBO
	ON PAUTONOMOS.FK_CBO = PCBO.ID_PCBO
ORDER BY 1, 2