SELECT FIN_BCO_AGE.ID_FIN_BCO_AGE,
       FIN_BCO.CODIGO AS CODIGO_BANCO,
       FIN_BCO_AGE.CODIGO AS CODIGO_AGENCIA,
       FIN_BCO_AGE.DG,
       FIN_BCO.DESCRICAO AS NOME_BANCO,
       FIN_BCO_AGE.DESCRICAO AS NOME_AGENCIA
FROM FIN_BCO_AGE
LEFT JOIN FIN_BCO
	ON FIN_BCO.ID_FIN_BCO = FIN_BCO_AGE.FK_BCO
ORDER BY 2, 3