SELECT CONTADOR.ID_CONTADOR,
       CONTADOR.NOME,
       CONTADOR.CRC,
       CONTADOR.INSCMF,
       CONTADOR.CNPJ_ESCRITORIO,
       TP_LOGRADOUROS.DESCRICAO AS TIPO_LOGRADOURO,
       CONTADOR.LOGRADOURO,
       CONTADOR.NUMERO,
       CONTADOR.COMPLEMENTO,
       CONTADOR.BAIRRO,
       CONTADOR.CEP,
       CIDADES.DESCRICAO AS CIDADE,
       ESTADOS.SIGLA AS ESTADO,
       CIDADES.CODIGO_IBGE,
       CONTADOR_TELEFONE.NUMERO AS TELEFONE,
       CONTADOR_EMAIL.ENDERECO AS EMAIL
FROM CONTADOR
LEFT JOIN CIDADES
	ON CIDADES.ID_CIDADES = CONTADOR.FK_CIDADE
LEFT JOIN ESTADOS
	ON ESTADOS.ID_ESTADOS = CIDADES.FK_ESTADO
LEFT JOIN TP_LOGRADOUROS
	ON TP_LOGRADOUROS.ID_TP_LOGRADOUROS = CONTADOR.FK_TP_LOGRADOURO
LEFT JOIN CONTADOR_TELEFONE
	ON CONTADOR_TELEFONE.FK_CONTADOR = CONTADOR.ID_CONTADOR
LEFT JOIN CONTADOR_EMAIL
	ON CONTADOR_EMAIL.FK_CONTADOR = CONTADOR.ID_CONTADOR
