SELECT SOCIOS.FK_PESSOA,
       SOCIOS.ID_SOCIOS,
       SOCIOS.MATRICULA,
       SOCIOS.NOME,
       SOCIOS.DATA_ENTRADA,
       SOCIOS.DATA_SAIDA,
       SOCIOS.DATA_NASCIMENTO,
       SOCIOS.SEXO,
       SOCIOS.ESTADO_CIVIL,
       SOCIOS.RACA_COR,
       SOCIOS.INSTRUCAO,
       SOCIOS.NOME_MAE,
       SOCIOS.NOME_PAI,
       SOCIOS.E_MAIL,
       SOCIOS.TELEFONE,
	   TP_LOGRADOUROS.DESCRICAO AS TIPO_LOGRADOURO,
       SOCIOS."LOGRADOURO",
       SOCIOS.COMPLEMENTO,
       SOCIOS."NUMERO" AS NUMERO_ENDERECO,
       SOCIOS."BAIRRO",
       SOCIOS.FK_CIDADE,
       SOCIOS.CEP,
       SOCIOS.FK_CIDADE_NASCIMENTO,
       SOCIOS.DATA_CHEGADA_BRASIL,
       SOCIOS.CONTA_PGTO,
       SOCIOS.DG_CONTA_PGTO,
       SOCIOS.FK_BCO_AGE_PGTO,
       SOCIOS.INSCMF,
       SOCIOS.INSC_INSS,
       SOCIOS.CTPS,
       SOCIOS.CTPS_SERIE,
       EST_CTPS.SIGLA AS UF_CTPS,
       SOCIOS.RG_NUMERO,
       RG_ORGAO_EMISSOR.SIGLA AS ORGAO_RG,
       SOCIOS.RG_DATA_EXPEDICAO,
       SOCIOS.RIC_RNE_NUMERO,
       SOCIOS.RIC_RNE_DATA_EXPEDICAO,
       PCARGO_FUNCAO.CODIGO AS CARGO
FROM SOCIOS
LEFT JOIN TP_LOGRADOUROS
	ON SOCIOS.FK_TP_LOGRADOURO = TP_LOGRADOUROS.ID_TP_LOGRADOUROS
LEFT JOIN RG_ORGAO_EMISSOR
	ON SOCIOS.FK_RG_ORGAO_EMISSOR = RG_ORGAO_EMISSOR.ID_RG_ORGAO_EMISSOR
LEFT JOIN ESTADOS EST_CTPS
	ON SOCIOS.FK_CTPS_UF = EST_CTPS.ID_ESTADOS
LEFT JOIN PCARGO_FUNCAO
	ON PCARGO_FUNCAO.FK_PESSOA = SOCIOS.FK_PESSOA
	AND PCARGO_FUNCAO.ID_PCARGO_FUNCAO = SOCIOS.FK_CARGO
ORDER BY 1, 3