SELECT DEP.FK_PESSOA,
       DEP.FK_FUNCIONARIO,
       DEP.NOME,
       DEP.TIPO_DEPENDENTE,
       DEP.DATA_NASCIMENTO,
       DEP.CARTORIO,
       DEP.REGISTRO,
       DEP.DATA_ENTREGA,
       DEP.LIVRO,
       DEP.FOLHA,
       DEP.CPF,
       DEP.DATA_FIN_SAL_FAM,
       DEP.DATA_FIN_IRRF,
	   CIDADES.DESCRICAO AS CIDADE,
	   ESTADOS.SIGLA AS ESTADO
FROM PFUNCIONARIO_DEPENDENTES AS DEP
LEFT JOIN CIDADES
	ON DEP.FK_CIDADE_NASCIMENTO = CIDADES.ID_CIDADES
LEFT JOIN ESTADOS
	ON CIDADES.FK_ESTADO = ESTADOS.ID_ESTADOS
ORDER BY 1, 2