SELECT DISTINCT PLOCAIS.FK_PESSOA,
       PLOCAIS.ID_PLOCAIS,
       PTOMADORES.CODIGO,
       PTOMADORES.NOME,
       PTOMADORES.INSCMF,
       TP_LOGRADOUROS.DESCRICAO AS TIPO_LOGRADOURO,
       PTOMADORES.LOGRADOURO,
       PTOMADORES.NUMERO,
       PTOMADORES.COMPL,
       PTOMADORES.BAIRRO,
	   CIDADES.DESCRICAO AS CIDADE,
	   ESTADOS.SIGLA AS ESTADO,
	   CIDADES.CODIGO_IBGE,
       PTOMADORES.CEP,
	   CNAE.CODIGO AS CNAE,
       PFPAS.CODIGO AS CODIGO_FPAS,
       PFPAS.PERCENTUAL AS INSS_EMPRESA,
       PCODIGO_TERCEIROS.CODIGO AS CODIGO_TERCEIRO,
	   PCODIGO_TERCEIROS.PERCENTUAL AS PERCENTUAL_TERCEIRO
FROM PFUNCIONARIO_SEC_LOCAL
INNER JOIN PLOCAIS
	ON PFUNCIONARIO_SEC_LOCAL.FK_PESSOA = PLOCAIS.FK_PESSOA
	AND PFUNCIONARIO_SEC_LOCAL.FK_LOCAl = PLOCAIS.ID_PLOCAIS
INNER JOIN PTOMADORES
	ON PTOMADORES.ID_PTOMADORES = PLOCAIS.FK_PTOMADOR
LEFT JOIN TP_LOGRADOUROS
	ON PTOMADORES.FK_TP_LOGRADOURO = TP_LOGRADOUROS.ID_TP_LOGRADOUROS
LEFT JOIN CIDADES
	ON PTOMADORES.FK_CIDADE = CIDADES.ID_CIDADES
LEFT JOIN ESTADOS
	ON CIDADES.FK_ESTADO = ESTADOS.ID_ESTADOS
LEFT JOIN PGPS
	ON PGPS.FK_PESSOA = PLOCAIS.FK_PESSOA
	AND PGPS.FK_LOCAL = PFUNCIONARIO_SEC_LOCAL.FK_LOCAl
LEFT JOIN PFPAS
	ON PFPAS.ID_PFPAS = PGPS.FK_FPAS
LEFT JOIN PESSOA_PESSOAL_HISTORICO
  	ON PLOCAIS.FK_PESSOA = PESSOA_PESSOAL_HISTORICO.FK_PESSOA
	AND PESSOA_PESSOAL_HISTORICO."DATA" = PGPS."DATA"
LEFT JOIN PFPAS_CODIGO_TERCEIROS
	ON PFPAS_CODIGO_TERCEIROS.ID_PFPAS_CODIGO_TERCEIROS = PESSOA_PESSOAL_HISTORICO.FK_FPAS_CODIGO_TERCEIROS
LEFT JOIN PCODIGO_TERCEIROS
	ON PCODIGO_TERCEIROS.ID_PCODIGO_TERCEIROS = PFPAS_CODIGO_TERCEIROS.FK_CODIGO_TERCEIROS
LEFT JOIN PESSOA_CNAE
	ON PESSOA_CNAE.FK_PESSOA = PLOCAIS.FK_PESSOA
	AND PESSOA_CNAE."DATA" = PGPS."DATA"
LEFT JOIN CNAE
	ON PESSOA_CNAE.FK_CNAE = CNAE.ID_CNAE
WHERE PLOCAIS.CODIGO <> 1
ORDER BY 1, 3