SELECT PSINDICATOS.ID_PSINDICATOS,
	   PSINDICATOS.CODIGO,
	   PSINDICATOS.DESCRICAO,
       PSINDICATOS.INSCMF,
       PSINDICATOS.COD_ENTIDADE,
       TP_LOGRADOUROS.DESCRICAO AS TIPO_LOGRADOURO,
       PSINDICATOS.ENDERECO,
       PSINDICATOS.NUMERO,
       PSINDICATOS.COMPL,
       PSINDICATOS.BAIRRO,
	   CIDADES.DESCRICAO AS CIDADE,
	   ESTADOS.SIGLA AS ESTADO,
       PSINDICATOS.CEP,
       PSINDICATOS.PATRONAL
FROM PSINDICATOS
LEFT JOIN TP_LOGRADOUROS
	ON PSINDICATOS.FK_TP_LOGRADOURO = TP_LOGRADOUROS.ID_TP_LOGRADOUROS
LEFT JOIN CIDADES
	ON PSINDICATOS.FK_CIDADE = CIDADES.ID_CIDADES
LEFT JOIN ESTADOS
	ON CIDADES.FK_ESTADO = ESTADOS.ID_ESTADOS
ORDER BY 2
