SELECT TROCA.FK_PESSOA,
       TROCA.FK_FUNCIONARIO,
       TROCA."DATA",
       PCARGO_FUNCAO.CODIGO AS NOVO_CARGO,
       TROCA.CARGA_HORARIA_SEMANAL,
       TROCA.CARGA_HORARIA_MENSAL
FROM PFUNCIONARIO_SEC_FUNCAO AS TROCA
LEFT JOIN PCARGO_FUNCAO
	ON PCARGO_FUNCAO.FK_PESSOA = TROCA.FK_PESSOA
	AND PCARGO_FUNCAO.ID_PCARGO_FUNCAO = TROCA.FK_FUNCAO
ORDER BY 1, 2, 3