SELECT TROCA.FK_PESSOA,
       TROCA.FK_FUNCIONARIO,
       TROCA.DATA,
       TROCA.FK_PHORARIO_TRABALHO_1,
       TROCA.FK_PHORARIO_TRABALHO_2,
       TROCA.FK_PHORARIO_TRABALHO_3,
       TROCA.FK_PHORARIO_TRABALHO_4,
       TROCA.FK_PHORARIO_TRABALHO_5,
       TROCA.FK_PHORARIO_TRABALHO_6,
       TROCA.FK_PHORARIO_TRABALHO_7
FROM PFUNCIONARIO_HORARIO AS TROCA
ORDER BY 1, 2, 3