SELECT TROCA.FK_PESSOA,
       TROCA.FK_SOCIOS,
       TROCA."DATA",
       PTOMADORES.CODIGO AS NOVO_SERVICO
FROM SOCIOS_SEC_LOCAL AS TROCA
INNER JOIN PLOCAIS
	ON TROCA.FK_PESSOA = PLOCAIS.FK_PESSOA
	AND TROCA.FK_LOCAl = PLOCAIS.ID_PLOCAIS
INNER JOIN PTOMADORES
	ON PTOMADORES.ID_PTOMADORES = PLOCAIS.FK_PTOMADOR
ORDER BY 1, 2, 3